mapper.select:
SELECT a.staySeq, a.stayName, a.stayZipCode, a.stayAddress, a.stayAddressDetail, a.stayLatitude, a.stayHardness, a.stayIntroMemo, a.stayServiceMemo, a.stayNoticeMemo, a.regDateTime, a.modDateTime, a.regionCd, a.activeCd, a.member_memSeq, a.stDeNy, a.stayPrice, region.ifcdSeq AS regionCd, region.ifcdName AS regionName, active.ifcdSeq AS activeCd, active.ifcdName AS activeName, b.memSeq FROM stay a
LEFT JOIN code region ON a.regionCd = region.ifcdSeq LEFT JOIN code active ON a.activeCd = active.ifcdSeq LEFT JOIN member b ON a.member_memSeq = b.memSeq
WHERE 1=1 AND a.stDeNy = 0 AND a.member_memSeq = #{memSeq}
